import React, { useState } from "react";
import { Link } from "react-router-dom";
import game from "../asset//maingame.jpg";

const Gamble = () => {
  const [playerMoney, setPlayerMoney] = useState(4);

  const oneDollar = () => {
    if (playerMoney < 1) {
      return "You do not have enough money";
    } else if (Math.random() > 0.5) {
      return setPlayerMoney(playerMoney + 1);
    } else if (Math.random() < 0.5) {
      return setPlayerMoney(playerMoney - 1);
    }
  };

  const threeDollar = () => {
    if (playerMoney < 3) {
      return "You do not have enough money";
    } else if (Math.random() > 0.5) {
      return setPlayerMoney(playerMoney + 3);
    } else if (Math.random() < 0.5) {
      return setPlayerMoney(playerMoney - 3);
    }
  };

  const fiveDollar = () => {
    if (playerMoney < 5) {
      return (
        <div style={{ position: "absolute", top: "30vh" }}>
          You do not have enough money
        </div>
      );
    } else if (Math.random() > 0.5) {
      return setPlayerMoney(playerMoney + 5);
    } else if (Math.random() < 0.5) {
      return setPlayerMoney(playerMoney - 5);
    }
  };

  const tenDollar = () => {
    if (playerMoney < 10) {
      return "You do not have enough money";
    } else if (Math.random() > 0.5) {
      return setPlayerMoney(playerMoney + 10);
    } else if (Math.random() < 0.5) {
      return setPlayerMoney(playerMoney - 10);
    }
  };

  return (
    <div className="gamble">
      <Link to="/gamelib" className="gamble__back">
        Back to Game Library
      </Link>
      <img className="gamble__img" src={game} alt="game" />
      <div>${playerMoney}</div>
      <button onClick={oneDollar}>$1</button>
      <button onClick={threeDollar}>$3</button>
      <button onClick={fiveDollar}>$5</button>
      <button onClick={tenDollar}>$10</button>
    </div>
  );
};

export default Gamble;
