import React, { useState } from "react";

const Gamble = () => {
  const [playerMoney, setPlayerMoney] = useState(100);

  // const  = (playerMoney) => {
  //   if (playerMoney < 1) {
  //     return "You are Broke";
  //   } else {
  //     return setPlayerMoney([playerMoney + 1] || [playerMoney - 1]);
  //   }
  // };

  const threeDollar = (playerMoney) => {
    if (playerMoney < 3) {
      return "You do not have enough money";
    } else {
      setPlayerMoney(playerMoney + 3 || playerMoney - 3);
    }
  };
  const fiveDollar = (playerMoney) => {
    if (playerMoney < 5) {
      return " You do not have enough money";
    } else {
      return setPlayerMoney(playerMoney + 5 || playerMoney - 5);
    }
  };
  const tenDollar = (playerMoney) => {
    if (playerMoney < 10) {
      return "You do not have enough money";
    } else {
      return setPlayerMoney(playerMoney + 10 || playerMoney - 10);
    }
  };

  const oneDollar = () => {
    if (playerMoney < 10) {
      return "You do not have enough money";
    } else if (Math.random() > 0.5) {
      return setPlayerMoney(playerMoney + 1);
    } else if (Math.random() < 0.5) {
      return setPlayerMoney(playerMoney - 1);
    }
  };

  return (
    <div className="gamble">
      <img />
      <div>${playerMoney}</div>
      <button onClick={oneDollar}>$1</button>
      <button
        onClick={() => setPlayerMoney(playerMoney + 3 || playerMoney - 3)}
      >
        $3
      </button>
      <button
        onClick={() =>
          setPlayerMoney(Math.random(playerMoney + 5 || playerMoney - 5))
        }
      >
        $5
      </button>
      <button
        onClick={() => setPlayerMoney(playerMoney + 10 || playerMoney - 10)}
      >
        $10
      </button>
    </div>
  );
};

export default Gamble;
