import React, { useState } from "react";

const Gamble = () => {
  const [playerMoney, setPlayerMoney] = useState(100);

  const oneDollar = (playerMoney) => {
    if (playerMoney < 1) {
      return "You are Broke";
    } else {
      return setPlayerMoney(playerMoney + 1 || playerMoney - 1);
    }
  };

  const threeDollar = (playerMoney) => {
    if (playerMoney < 3) {
      return "You do not have enough money";
    } else {
      return playerMoney + 3 || playerMoney - 3;
    }
  };
  const fiveDollar = (playerMoney) => {
    if (playerMoney < 5) {
      return " You do not have enough money";
    } else {
      return playerMoney + 5 || playerMoney - 5;
    }
  };
  const tenDollar = (playerMoney) => {
    if (playerMoney < 10) {
      return "You do not have enough money";
    } else {
      return playerMoney + 10 || playerMoney - 10;
    }
  };

  return (
    <div className="gamble">
      <img />
      <button onPress={oneDollar}>$1</button>
      <button onPress={threeDollar}>$3</button>
      <button onPress={fiveDollar}>$5</button>
      <button onPress={tenDollar}>$10</button>
    </div>
  );
};

export default Gamble;
