import React, { useState, useRef } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Link, Redirect } from "react-router-dom";

import Form from "react-validation/build/form";
import Input from "react-validation/build/input";
import CheckButton from "react-validation/build/button";

import { login } from "../actions/auth";

const required = (value) => {
  if (!value) {
    return (
      <div className="alert alert-danger" role="alert">
        This field is required!
      </div>
    );
  }
};

const SignIn = (props) => {
  const form = useRef();
  const checkBtn = useRef();

  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [loading, setLoading] = useState(false);

  const { isLoggedIn } = useSelector((state) => state.auth);
  const { message } = useSelector((state) => state.message);

  const dispatch = useDispatch();

  const onChangeUsername = (e) => {
    const username = e.target.value;
    setUsername(username);
  };

  const onChangePassword = (e) => {
    const password = e.target.value;
    setPassword(password);
  };

  const handleLogin = (e) => {
    e.preventDefault();

    setLoading(true);

    form.current.validateAll();

    if (checkBtn.current.context._errors.length === 0) {
      dispatch(login(username, password))
        .then(() => {
          props.history.push("/gamelib");
          window.location.reload();
        })
        .catch(() => {
          setLoading(false);
        });
    } else {
      setLoading(false);
    }
  };
  if (isLoggedIn) {
    return <Redirect to="/gamelib" />;
  }

  return (
    <div className="signin">
      <Link to="/" className="signin__back">
        Back
      </Link>
      <Form className="signin__form" onSubmit={handleLogin} ref={form}>
        <div className="signin__name">
          <label className="signin__label" htmlFor="username">
            Username
          </label>
          <Input
            type="text"
            className="signin__name--input"
            name="username"
            value={username}
            onChange={onChangeUsername}
            validattions={[required]}
            placeholder="Enter Username"
          />
        </div>
        <div className="signin__password">
          <label className="signin__label" htmlFor="password">
            Password
          </label>
          <Input
            type="password"
            className="signin__password--input"
            name="password"
            value={password}
            onChange={onChangePassword}
            validattions={[required]}
          />
        </div>
        <div className="signin__button">
          <button className="btn btn-primary btn-block" disabled={loading}>
            {loading && (
              <span className="spinner-border spinner-border-sm"></span>
            )}
            <span>Login</span>
          </button>
          {message && (
            <div className="form-group">
              <div className="alert alert-danger" role="alert">
                {message}
              </div>
            </div>
          )}
        </div>
      </Form>
    </div>
  );
};

export default SignIn;
